  --chart-1: hsl(220 70% 50%);
  --chart-2: hsl(160 60% 45%);
  --chart-3: hsl(30 80% 55%);
  --chart-4: hsl(280 65% 60%);
  --chart-5: hsl(340 75% 55%);
  --sidebar: hsl(215.4 16.3% 16.9%);
  --sidebar-foreground: hsl(210 40% 98%);
  --sidebar-primary: hsl(217.2 91.2% 59.8%);
  --sidebar-primary-foreground: hsl(0 0% 100%);
  --sidebar-accent: hsl(215.3 13.8% 34.1%);
  --sidebar-accent-foreground: hsl(210 40% 98%);
  --sidebar-border: hsl(215.4 16.3% 16.9%);
  --sidebar-ring: hsl(217.2 91.2% 59.8%);
  --font-sans: Inter, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
  --radius: 1.3rem;
  --shadow-2xs: 0px 2px 0px 0px hsl(217.2 91.2% 59.8% / 0.00);
  --shadow-xs: 0px 2px 0px 0px hsl(217.2 91.2% 59.8% / 0.00);
  --shadow-sm: 0px 2px 0px 0px hsl(217.2 91.2% 59.8% / 0.00), 0px 1px 2px -1px hsl(217.2 91.2% 59.8% / 0.00);
  --shadow: 0px 2px 0px 0px hsl(217.2 91.2% 59.8% / 0.00), 0px 1px 2px -1px hsl(217.2 91.2% 59.8% / 0.00);
  --shadow-md: 0px 2px 0px 0px hsl(217.2 91.2% 59.8% / 0.00), 0px 2px 4px -1px hsl(217.2 91.2% 59.8% / 0.00);
  --shadow-lg: 0px 2px 0px 0px hsl(217.2 91.2% 59.8% / 0.00), 0px 4px 6px -1px hsl(217.2 91.2% 59.8% / 0.00);
  --shadow-xl: 0px 2px 0px 0px hsl(217.2 91.2% 59.8% / 0.00), 0px 8px 10px -1px hsl(217.2 91.2% 59.8% / 0.00);
  --shadow-2xl: 0px 2px 0px 0px hsl(217.2 91.2% 59.8% / 0.00);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased;
    background: var(--game-dark);
    color: var(--game-text);
    font-family: var(--font-sans);
  }
}

.glass-effect {
  backdrop-filter: blur(10px);
  background: hsl(215.3 13.8% 34.1% / 0.8);
}

.gradient-bg {
  background: linear-gradient(135deg, var(--game-dark) 0%, var(--game-navy) 100%);
}

/* Game specific styles */
.game-card {
  background: hsl(var(--game-navy) / 0.5);
  backdrop-filter: blur(4px);
  @apply rounded-xl border border-gray-700/50 transition-all duration-200 cursor-pointer;
}

.game-card:hover {
  transform: translateY(-2px);
}

.game-button {
  background: linear-gradient(to right, hsl(var(--game-blue)), hsl(217 91% 60%));
  @apply text-white font-semibold py-3 px-6 rounded-lg transition-all duration-200 transform hover:scale-[1.02];
}

.game-button-secondary {
  @apply bg-gray-600/50 text-gray-300 font-semibold py-3 px-6 rounded-lg hover:bg-gray-600/70 transition-all duration-200 border border-gray-600;
}

.connection-indicator {
  @apply flex items-center space-x-2;
}

.connection-indicator.online .dot {
  @apply w-2 h-2 bg-green-500 rounded-full animate-pulse;
}

.connection-indicator.offline .dot {
  @apply w-2 h-2 bg-red-500 rounded-full;
}

.player-avatar {
  @apply w-12 h-12 rounded-full flex items-center justify-center text-white font-bold;
}

.chat-message {
  @apply text-sm;
}

.chat-message .sender {
  @apply font-medium;
}

.chat-message .text {
  @apply text-gray-300 ml-2;
}

/* Drag and drop enhancements */
.dragging {
  @apply cursor-grabbing;
}

.drag-preview {
  @apply transform rotate-6 scale-110 opacity-75;
}

/* Touch-friendly game pieces */
@media (hover: none) and (pointer: coarse) {
  [draggable="true"] {
    @apply touch-none;
  }

  .game-board-square {
    @apply min-h-[3rem] min-w-[3rem];
  }

  .chess-piece, .checkers-piece {
    @apply transform scale-110;
  }
}

/* Game board improvements */
.game-board {
  @apply touch-none select-none transition-all duration-300;
}

.game-board.loading {
  @apply opacity-50 pointer-events-none;
}

.game-square {
  @apply transition-all duration-200 ease-in-out;
}

.game-square:hover {
  @apply transform scale-105 brightness-110;
}

.game-square.highlighted {
  @apply ring-2 ring-yellow-400 ring-opacity-75;
  animation: pulse 2s ease-in-out infinite;
}

.game-piece {
  @apply transition-all duration-200 ease-in-out;
}

.game-piece:active {
  @apply scale-95;
}

.game-piece.dragging {
  @apply opacity-50 scale-110 z-50;
}

/* Loading states and animations */
@keyframes pulse-slow {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.3; }
}

@keyframes spin-slow {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

@keyframes bounce-subtle {
  0%, 20%, 53%, 80%, 100% {
    transform: translate3d(0, 0, 0);
  }
  40%, 43% {
    transform: translate3d(0, -8px, 0);
  }
  70% {
    transform: translate3d(0, -4px, 0);
  }
  90% {
    transform: translate3d(0, -2px, 0);
  }
}

.loading-pulse {
  animation: pulse-slow 2s ease-in-out infinite;
}

.loading-spin {
  animation: spin-slow 1s linear infinite;
}

.loading-bounce {
  animation: bounce-subtle 1s ease-in-out infinite;
}

/* Skeleton loading states */
.skeleton {
  @apply bg-gray-600/30 animate-pulse rounded;
}

.skeleton-text {
  @apply h-4 bg-gray-600/30 animate-pulse rounded;
}

.skeleton-avatar {
  @apply w-8 h-8 bg-gray-600/30 animate-pulse rounded-full;
}

/* Enhanced error states */
.error-shake {
  animation: shake 0.5s ease-in-out;
}

@keyframes shake {
  0%, 100% { transform: translateX(0); }
  25% { transform: translateX(-5px); }
  75% { transform: translateX(5px); }
}

/* Connection status animations */
.connection-pulse {
  animation: pulse 2s ease-in-out infinite;
}

.connection-reconnecting {
  animation: pulse 1s ease-in-out infinite;
}

/* Game piece selection highlight */
.piece-selected {
  @apply ring-4 ring-blue-400 ring-opacity-75;
  animation: pulse 1s ease-in-out infinite;
}

.piece-valid-move {
  @apply ring-2 ring-green-400 ring-opacity-50;
  background: radial-gradient(circle, rgba(34, 197, 94, 0.2) 0%, transparent 50%);
}

/* Toast notification improvements */
.toast-success {
  @apply border-green-500/50 bg-green-950/90;
}

.toast-error {
  @apply border-red-500/50 bg-red-950/90;
  animation: shake 0.3s ease-in-out;
}

.toast-info {
  @apply border-blue-500/50 bg-blue-950/90;
}

/* Chess board styling */
.chess-light-square {
  background-color: #f0d9b5;
}

.chess-dark-square {
  background-color: #b58863;
}

/* Chess piece contrast improvements */
.chess-piece {
  font-weight: bold;
  font-size: 1.5rem;
  transition: all 0.2s ease-in-out;
}

.chess-piece:hover {
  transform: scale(1.1);
}

.chess-piece.white-piece {
  color: #ffffff;
  text-shadow: 
    -2px -2px 0 #000,
    2px -2px 0 #000,
    -2px 2px 0 #000,
    2px 2px 0 #000,
    -3px 0 0 #000,
    3px 0 0 #000,
    0 -3px 0 #000,
    0 3px 0 #000;
  filter: drop-shadow(2px 2px 4px rgba(0,0,0,0.9)) drop-shadow(-1px -1px 1px rgba(0,0,0,0.5));
  background: linear-gradient(45deg, #ffffff, #f5f5f5);
  -webkit-background-clip: text;
  background-clip: text;
}

.chess-piece.black-piece {
  color: #1a1a1a;
  text-shadow: 
    -1px -1px 0 #fff,
    1px -1px 0 #fff,
    -1px 1px 0 #fff,
    1px 1px 0 #fff;
  filter: drop-shadow(1px 1px 2px rgba(255,255,255,0.4));
}

